$(document).ready(function(){var smooth='scrollBehavior'in document.documentElement.style;var spaceToTop=50;$('*[data-scrollto]').on('click',function(){var pos=$('#'+$(this).attr('data-scrollto'))[0].offsetTop-spaceToTop;if(smooth){window.scrollTo({top:pos,behavior:'smooth'});}else{window.scrollTo(0,pos);}
return false;});});$(function(){$('[data-toggle="popover"]').popover();});$(document).ready(function(){$('.nospam-email').each(function(){$(this).html($(this).html().replace('_(a)_','@'),);});$('.nospam-phone').each(function(){$(this).html($(this).html().replace(new RegExp('_','g'),''),);});});$('#cookie-notice .btn').click(function(){Cookies.set('cookie-notice','hide',{expires:90});$('#cookie-notice').remove();});if(!Cookies.get('cookie-notice')){$('#cookie-notice').show();}
function mapSm(s){switch(s){case 'kb':return 'facebook';case 'wt':return 'twitter';case 'kd':return 'linkedin';case 'lw':return 'newsletter';case 'gx':return 'xing';case 'ag':return 'googleads';case 'pf':return 'pdf';case 'ct':return 'capterra';case 'sh':return 'softwaretestinghelp';case 'ww':return 'website';case 'bb':return 'blog';}}
function extractQueryParams(){var url=window.location.search;if(url.length===0){return{};}
url=url.substring(1);var parts=url.split('&');var result={};parts.forEach(function(part){var element=part.split('=');if(element.length!==2){return;}
result[element[0]]=decodeURIComponent(element[1]);});return result;}
function getCampaignType(source){if(!source){return 'ww';}
source=source.toLowerCase();if(source==='facebook'){return 'kb';}
if(source==='twitter'){return 'wt';}
if(source==='linkedin'){return 'kd';}
if(source==='newsletter'){return 'lw';}
if(source==='xing'){return 'gx';}
if(source==='googleads'){return 'ag';}
if(source==='pdf'){return 'pf';}
if(source==='capterra'){return 'ct';}
if(source==='softwaretestinghelp'){return 'sh';}
return 'ww';}
function getCampaignId(campaign){if(!campaign){return '';}
var num=parseInt(campaign);if(isNaN(num)){return '';}
return num.toString();}
$(document).ready(function(){var params=extractQueryParams();var source=Cookies.get('_sm');if(!source){source=getCampaignType(params['utm_source']);Cookies.set('_sm',source,{domain:'ripstech.com',expires:90});}
var campaign=Cookies.get('_cm');if(!campaign){campaign=getCampaignId(params['utm_campaign']);Cookies.set('_cm',campaign,{domain:'ripstech.com',expires:90});}});